
public class Ejercicio1 {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		
		short x=144;
		short y=999;
		int suma=x+y;
		int resta=x-y;
		int multiplicacion=x*y;
		int division=y/x;
		System.out.println("La suma de las dos variables es " +suma);
		System.out.println("La resta de las dos variables es " +resta);
		System.out.println("La multiplicacion de las dos variables es " +multiplicacion);
		System.out.println("La divison de las dos variables es " +division);
		
		
	}

}
